import React, { useState } from 'react';
import { View, Animated, Dimensions, Text,FlatList, ScrollView, GestureResponderEvent } from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import { listOfChatsStyle } from '../Styles/ListOfChatsStyle';
import ChatContainer from './List of folders containers/ChatContainer';



interface ListOfFolderProps {
  user: MySelfUser;
  selectedFolder: number;
}

const ListOfFolder: React.FC<ListOfFolderProps> = ({ user, selectedFolder }) => {
  const { width: screenWidth, height: screenHeight } = Dimensions.get('window');
  const handleLongPressForChatModalWindow = (e: GestureResponderEvent, index: number) => {
    console.log(index)
  }
  return (
    <>
    <ScrollView nestedScrollEnabled={true}>
    <View style={listOfChatsStyle.gapContainerHigh} />
          <View style={listOfChatsStyle.lineStyle} />
     {user.folders[selectedFolder].listOfChats.map((chat, index) => (
    <ChatContainer key={index} chat={chat} onPress={()=>{}} handleLongPress={(event)=>handleLongPressForChatModalWindow(event,index)}/>
  ))}
  <View style={listOfChatsStyle.gapContainerDown} />
</ScrollView>


    </>);
};

export default ListOfFolder;
