import React from 'react';
const { width: screenWidth ,height:screenHeight} = Dimensions.get('window');
export const listOfChatsStyle= StyleSheet.create({
    container:{
        //backgroundColor:"red",

    },
    chatcontainer:{
        flexDirection:"row",
        height:screenHeight*0.08,
        borderBottomColor:"black",
        borderBottomWidth:1
    
    },
    lineStyle:{
        borderBottomColor: '#C4A56899',
        borderBottomWidth: screenWidth*0.02,
    },
    image:{
    height:screenHeight*0.08*0.7,
    width:screenHeight*0.08*0.7,
    aspectRatio: 1,
    alignSelf:"center",
    borderRadius:1000,
    },
    gapContainerHigh:{
        height:screenHeight*0.085
    },
    gapContainerDown:{
        height:screenHeight*0.08
    }
    ,
    containerForOther:{
        flex:1,
        //backgroundColor:"white",
        marginLeft:screenWidth*0.01,
       // alignItems: "flex-start",

    },
    containerForContent:{
        width:screenWidth*0.7,
        //backgroundColor:"white",
        flex:1
    } ,
    namecontainer:{
        width:screenWidth*0.7,
        marginTop:screenHeight*0.005,
        marginBottom:screenHeight*0.003
        //backgroundColor:"blue"
    }
    ,
    rightContainer:{
        backgroundColor:"white",
        flex:1,
        flexDirection:"row"
    },
    highcontainer:{
        flexDirection:"row",
    },
    nameStyle:{
        fontWeight:"600",
       fontSize:screenHeight*0.017
    
    },
    contentStyle:{
        fontSize:screenHeight*0.015,
    },
    timeContainer:{
        justifyContent:"center",
        alignSelf:"center",
        backgroundColor:"gray"
    },
    timeStyle:{
        alignSelf:"center",
        fontSize:screenHeight*0.01
    },
    lastMessageStatusContainer:{
        alignSelf:"center",
        justifyContent:"center",
        flexDirection:"row",
        backgroundColor:"red",
        flex:1
    },
    countOfUnreadMessagescontainer:{
        backgroundColor:"#339EDA",
        borderRadius:100,
        width:screenWidth*0.03,
        height:screenWidth*0.03,
        justifyContent:"center",
        alignSelf:"center",
    },
    countOfUnReadMessagesContent:{
        fontSize:screenWi*0.01,
        alignSelf:'center',
        justifyContent:'center',
        color:"#FFFFFF"
    }

});
