import React, { useMemo, useState } from 'react';
import { View, StyleSheet, Image, Text, TouchableOpacity,Dimensions,ScrollView,Animated,TouchableHighlight,StatusBar,Easing} from 'react-native';
import { listOfChatsStyle } from '../../Styles/ListOfChatsStyle';
import Message from '../../1HelpFullFolder/Message';
import Chat from '../../1HelpFullFolder/Chat';
import LastMessageStatus from './LastMessageStatus';
import ModeActivity from '../Status Content/ModeActivity';
import { GestureHandlerRootView, Swipeable } from "react-native-gesture-handler";
import {SwipeListView} from 'react-native-swipe-list-view';
import { useSharedValue } from 'react-native-reanimated';
interface ChatProps {
  chat: Chat;
}
const { width: screenWidth ,height:screenHeight} = Dimensions.get('window');
const ChatContainer: React.FC<ChatProps> = ({ chat }) => {
  const lastMessage: Message | undefined = useMemo(() => {
    return chat.listOfMessages.length > 0 ? chat.listOfMessages[chat.listOfMessages.length - 1] : undefined;
  }, [chat.listOfMessages]);
  const formattedTime: string = useMemo(() => {
    if (!lastMessage) return '';
    const now: Date = new Date();
    const timeDiff: number = now.getTime() - lastMessage.timeOfSend.getTime();
    const dayInMilliseconds: number = 24 * 60 * 60 * 1000;
    const weekInMilliseconds: number = 7 * dayInMilliseconds;
    const yearInMilliseconds: number = 365 * dayInMilliseconds;
    const minutes: number = lastMessage.timeOfSend.getMinutes();
    const hours: number = lastMessage.timeOfSend.getHours();
    const day: number = lastMessage.timeOfSend.getDate();
    const month: number = lastMessage.timeOfSend.getMonth();
    const minutesString: string = minutes < 10 ? `0${minutes}` : minutes.toString();
    const hoursString: string = hours < 10 ? `0${hours}` : hours.toString();
    const daySting: string = day < 10 ? `0${day}` : day.toString();
    const monthString: string = month < 10 ? `0${month}` : month.toString();

    if (timeDiff < dayInMilliseconds) {
      return `${hoursString}:${minutesString}`;
    } else if (timeDiff < weekInMilliseconds) {
      const daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
      const dayOfWeek: string = daysOfWeek[lastMessage.timeOfSend.getDay()];
      return dayOfWeek.slice(0, 3);
    } else if (timeDiff < yearInMilliseconds) {
      return `${daySting}.${monthString}`;
    } else {
      return `${daySting}.${monthString}.${lastMessage.timeOfSend.getFullYear()-2000}`;
    }
  }, [lastMessage]);
  
  const handlePress = () => {
    
    console.log('Кнопку натиснули');
  };

  const handleLongPress = () => {
    // Дії при утриманні
    console.log('Кнопку утримують');
  };
  const handleSwipeRight = () => {
    
    console.log('Swipe вправо');
  };

  const handleSwipeLeft = () => {
    
    console.log('Swipe вліво');
  };
  const renderLeftActions = (progress:any, dragX:any) => {
    const scale=dragX.interpolate({
      inputRange:[0,100],
      outputRange:[0,1],
      extrapolate:"clamp"
    })
    return (
      <View style={listOfChatsStyle.leftAction}>
        <Animated.Text style={[listOfChatsStyle.actionText,{transform:[{scale}]}]}>
          Select
        </Animated.Text>
      </View>
    )
  };
  const renderRightActions= (progress:any, dragX:any) => {
    const animatedValue = useSharedValue(0);
    /*Animated.timing(animatedValue.value,{
      toValue: 0,
      duration: 200,
      useNativeDriver: false,
    })
    });*/
  

  return (
    <Animated.View
      style={[, {height: rowHeightAnimatedValue}]}>
      <TouchableHighlight
        style={styles.rowFrontVisible}
        onPress={() => console.log('Element touched')}
        underlayColor={'#aaa'}>
        <View>
          <Text style={styles.title} numberOfLines={1}>
            {data.item.title}
          </Text>
          <Text style={styles.details} numberOfLines={1}>
            {data.item.details}
          </Text>
        </View>
      </TouchableHighlight>
    </Animated.View>
  );
};
return(
  <View>
  <TouchableOpacity 
  onPress={handlePress} 
  onLongPress={handleLongPress}
  pressRetentionOffset={{ top: 0, left: 0, right: 0, bottom: 0 }}
  activeOpacity={1}
  >
    <View style={listOfChatsStyle.helpContainer}/>
    <GestureHandlerRootView >
      <Swipeable renderRightActions={renderRightActions}
        renderLeftActions={renderLeftActions}
        onSwipeableRightOpen={handleSwipeRight}
        onSwipeableLeftOpen={handleSwipeLeft}
        >
    <View   style={listOfChatsStyle.chatcontainer}>
      <View style={[listOfChatsStyle.imageContainer ]} >
      <Image source={{ uri: chat.url }} style={listOfChatsStyle.image}></Image>
      <ModeActivity style={listOfChatsStyle.modeOfActivity} status={chat.status}/>
      </View>
      <View style={listOfChatsStyle.containerForOther}>
        <View style={listOfChatsStyle.highcontainer}>
          <View style={listOfChatsStyle.namecontainer}> 
          <Text style={listOfChatsStyle.nameStyle} numberOfLines={1} ellipsizeMode="tail">{chat.name}</Text>
          </View>
          <View style={[listOfChatsStyle.rightContainer]} >
            <LastMessageStatus chat={chat}/>
             <Text style={listOfChatsStyle.timeStyle}>{formattedTime}</Text>
      </View>
        </View>
        <View style={listOfChatsStyle.containerForContent}>
          <Text style={listOfChatsStyle.contentStyle} numberOfLines={2} ellipsizeMode="tail">{lastMessage?.content}</Text>
        </View>
      </View>
    </View>
    </Swipeable>
    </GestureHandlerRootView>
    </TouchableOpacity>
    </View>
  );
};


export default React.memo(ChatContainer);
