// Viktor Hraboviuk

import React from "react";
import { Text, View, TouchableOpacity } from "react-native";
import { useNavigation } from "@react-navigation/native";
import { LinearGradient } from "expo-linear-gradient";
import { pickedProfile } from "./DatabaseSimulation/DBVariables";

export default function ChooseAuthor() {
  const navigation = useNavigation();

  return (
    <LinearGradient
      colors={["#cf9b95", "#c98bb8", "#c37adb"]}
      style={{ flex: 1 }}
    >
      {/* Group */}
      <TouchableOpacity
        style={{
          height: "33.333%",
          alignItems: "center",
          justifyContent: "center",
        }}
        onPress={() => {
          pickedProfile.current = "group";
          navigation.navigate("GroupNavigation" as never);
        }}
      >
        <Text style={{ fontSize: 40, color: "white" }}>Group</Text>
      </TouchableOpacity>

      {/* User */}
      <TouchableOpacity
        style={{
          height: "33.333%",
          alignItems: "center",
          justifyContent: "center",
        }}
        onPress={() => {
          pickedProfile.current = "user";
          navigation.navigate("UserNavigation" as never);
        }}
      >
        <Text style={{ fontSize: 40 }}>User</Text>
      </TouchableOpacity>

      {/* Channel */}
      <TouchableOpacity
        style={{
          height: "33.333%",
          alignItems: "center",
          justifyContent: "center",
        }}
        onPress={() => {
          pickedProfile.current = "channel";
          navigation.navigate("ChannelNavigation" as never);
        }}
      >
        <Text style={{ fontSize: 40 }}>Channel</Text>
      </TouchableOpacity>
    </LinearGradient>
  );
}
