import React from 'react';
import { View, Text } from 'react-native';
import Chat from '../../1HelpFullFolder/Chat';
import Message from '../../1HelpFullFolder/Message';

interface LastMessageStatusProps {
  chat: Chat;
}

const LastMessageStatus: React.FC<LastMessageStatusProps> = ({ chat }) => {
  const lastMessage: Message | undefined = chat.listOfMessages[chat.listOfMessages.length - 1];

  if (!lastMessage) {
    return <View><Text>No messages</Text></View>;
  }

  return (
    <View>
      {lastMessage.sender === myselfUser ? (
        <Text>Sent</Text>
      ) : (
        <Text>Received</Text>
      )}
    </View>
  );
};

export default LastMessageStatus;
