import React, { useContext } from 'react';
import { View, ScrollView } from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import ChatContainer from './List of folders containers/ChatContainer';
import { listOfChatsStyle } from '../Styles/ListOfChatsStyle';
import { sel } from './Footer';

interface ListOfFolderProps {
  user: MySelfUser;
}

const ListOfFolder: React.FC<ListOfFolderProps> = ({ user }) => {
  let selectedFolder = useContext(SelectedFolderContext);
  if (selectedFolder === null) {
    selectedFolder=0;
}
  return (
    <ScrollView showsHorizontalScrollIndicator={false} style={listOfChatsStyle.container}>
      <View style={listOfChatsStyle.gapContainer} />
      {user.folders[selectedFolder].listOfChats.map((chat, index) => (
        <ChatContainer chat={chat} key={index} />
      ))}
      <View style={listOfChatsStyle.gapContainer} />
    </ScrollView>
  );
};

export default ListOfFolder;
