import React, { useState } from "react";
import {
  View,
  Image,
  TouchableOpacity,
  GestureResponderEvent,
  Animated,
  Dimensions,
  ScrollView
} from "react-native";
import { headerstyles } from "../Styles/HeaderStyle";
import MagnifyingGlass from "./Headers containers/MagnifyingGlass";
import ModeOfEmployment from "./Headers containers/ModeOfEmployment";
import Line from "./Headers containers/Line";
import MySelfUser from "../1HelpFullFolder/MySelfUser";
const { width: screenWidth, height: screenHeight } = Dimensions.get("window");
export default function Header({ mySelfUser }: { mySelfUser: MySelfUser }) {
  const timeForLineAnimation: number = 400;
  const rotateGradusOfLines: number = 45;
  const kefOfTransform: number = 0.8;
  const topLineValue = useState(new Animated.Value(0))[0];
  const middleLineValue = useState(new Animated.Value(1))[0];
  const bottomLineValue = useState(new Animated.Value(0))[0];
  const [state, setState] = useState(1);
  const topLineRotation = topLineValue.interpolate({
    inputRange: [0, 1],
    outputRange: ["0deg", `-${rotateGradusOfLines}deg`],
  });
  const bottomLineRotation = bottomLineValue.interpolate({
    inputRange: [0, 1],
    outputRange: ["0deg", `${rotateGradusOfLines}deg`],
  });
  const positionXOfTopLine = topLineValue.interpolate({
    inputRange: [0, 1],
    outputRange: [0, screenHeight * 0.01],
  });

  const positionYOfTopLine = topLineValue.interpolate({
    inputRange: [0, 1],
    outputRange: [0, 0],
  });

  const positionOfBottomLine = topLineValue.interpolate({
    inputRange: [0, 1],
    outputRange: [screenHeight, screenHeight * 0.9],
  });
  const hamburgerAnimation = (e: GestureResponderEvent) => {
    setState(state == 0 ? 1 : 0);
    lineTransformation(e);
  };
  const lineTransformation = (e: GestureResponderEvent) => {
    Animated.timing(topLineValue, {
      toValue: state,
      duration: timeForLineAnimation,
      useNativeDriver: false,
    }).start();
    Animated.timing(bottomLineValue, {
      toValue: state,
      duration: timeForLineAnimation,
      useNativeDriver: false,
    }).start();
    Animated.timing(middleLineValue, {
      toValue: state == 0 ? 1 : 0,
      duration: state == 1 ? 10 : timeForLineAnimation, // Змінив на 1000 мс (1 секунда) для прикладу
      useNativeDriver: false,
    }).start();
  };

  return (
    <View
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        right: 0,
        zIndex: 5,
      }}
    >
      <View style={headerstyles.gapContainer}  />
      <View style={headerstyles.container}>
        <ScrollView horizontal style={headerstyles.header} contentContainerStyle>
            <MagnifyingGlass style={headerstyles.magnifyingglass} />
            <ModeOfEmployment />
            <TouchableOpacity onPress={hamburgerAnimation}>
              <View style={headerstyles.hamburgerview}>
                <Animated.View
                  style={{
                    transform: [
                      { rotate: topLineRotation },
                      { translateX: positionXOfTopLine },
                      { translateY: positionYOfTopLine },
                    ],
                  }}
                >
                  <Line
                    screenWidth={
                      state == 0 ? screenWidth * kefOfTransform : screenWidth
                    }
                    screenHeight={screenHeight}
                  />
                </Animated.View>
                <Animated.View style={{ opacity: middleLineValue }}>
                  <Line screenWidth={screenWidth} screenHeight={screenHeight} />
                </Animated.View>
                <Animated.View
                  style={{
                    transform: [
                      { rotate: bottomLineRotation },
                      { translateX: positionXOfTopLine },
                      { translateY: positionYOfTopLine },
                    ],
                  }}
                >
                  <Line
                    screenWidth={
                      state == 0 ? screenWidth * kefOfTransform : screenWidth
                    }
                    screenHeight={screenHeight}
                  />
                </Animated.View>
              </View>
            </TouchableOpacity>
        </ScrollView>
      </View>
    </View>
  );
}
