import React, { useRef, useState, useEffect } from 'react';
import { View, ScrollView, StyleSheet, Text, Dimensions, Animated, TouchableOpacity, Easing } from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import { footerstyles } from '../Styles/FooterStyle';
import ListOfFolder from './ListOfFolder';
import Folder from './Footer containers/Folder';

interface FooterProps {
  user: MySelfUser;
}

const { width: screenWidth, height: screenHeight } = Dimensions.get('window');

const Footer: React.FC<FooterProps> = ({ user }) => {
  const [selectedFolder, setSelectedFolder] = useState<number>(0);
  const scrollX = useRef(new Animated.Value(0)).current;
  const [currentPosition, setCurrentPosition] = useState(0);

  const handleFolderPress = (index: number) => {
    if (selectedFolder === index) {
      return;
    }
    setSelectedFolder(index);
    scrollToFolder(index);
  };

  const updateSelectedFolder = () => {
    const newSelectedFolder = Math.floor(currentPosition / screenWidth);
    setSelectedFolder(newSelectedFolder);
  };

  const handleHorizontalScroll = (event: any) => {
    const newHorizontalPosition = event.nativeEvent.contentOffset.x;
    setCurrentPosition(newHorizontalPosition);
  };

  const handleMomentumScrollEnd = () => {
    updateSelectedFolder();
  };
  
  const scrollToFolder = (folderId: number) => {
  
   scrollViewRef.current?.scrollTo({ x: folderId * screenWidth,animated:true});
  };

  useEffect(() => {
    scrollX.addListener(({ value }) => {
      setCurrentPosition(value);
    });
    return () => {
      scrollX.removeAllListeners();
    };
  }, []);

  const scrollViewRef = useRef<ScrollView | null>(null);
  const [width, setWidth] = useState(0);
  const handleLayout = (event) => {
    const { width } = event.nativeEvent.layout; // Отримуємо ширину з події
    setWidth(width); // Зберігаємо ширину в стані
  };
  return (<>
    <ScrollView
      horizontal
      scrollEventThrottle={100}
      showsHorizontalScrollIndicator={false}
      ref={scrollViewRef}
      pagingEnabled
      onScroll={handleHorizontalScroll}
      onMomentumScrollEnd={handleMomentumScrollEnd}
      nestedScrollEnabled={true}
    >
      {user.folders.map((folder, index) => (
        <ListOfFolder key={index} user={user} selectedFolder={index} />
      ))}
    </ScrollView>
    <View style={footerstyles.container}>
      <ScrollView
        showsHorizontalScrollIndicator={false}
        horizontal
        style={footerstyles.scrollView}
        scrollEventThrottle={100}
      >
        {user.folders.map((folder, index) => (
          <Folder
            key={index}
            text={folder.name}
            containerStyle={footerstyles.folderContainer}
            textStyle={footerstyles.folder}
            isSelected={selectedFolder === index}
            onPress={()=> handleFolderPress(index)}
          />
        ))}
        <View style={[footerstyles.selectedFolder,{left:currentPosition}]}/>
      </ScrollView>
    </View>
    </>
  );
};

export default Footer;
