import React, { Component, useMemo, useRef, useState } from 'react';
import { View, StyleSheet, Image, Text, TouchableOpacity,Dimensions,ScrollView, PanResponder,Animated, PanResponderInstance, GestureResponderEvent, LayoutChangeEvent, PointerEvent } from 'react-native';
import { listOfChatsStyle } from '../../Styles/ListOfChatsStyle';
import Message from '../../1HelpFullFolder/Message';
import Chat from '../../1HelpFullFolder/Chat';
import LastMessageStatus from './LastMessageStatus';
import ModeActivity from '../Status Content/ModeActivity';
import { GestureHandlerRootView, RectButton,Swipeable} from "react-native-gesture-handler";

interface ChatProps {
  chat: Chat;
}
interface ForState{
  isSwiped:boolean
}

const { width: screenWidth ,height:screenHeight} = Dimensions.get('window');
class ChatContainer extends React.Component<ChatProps,ForState>{
  private chat:Chat;
  private lastMessage: Message | undefined;
  private position:any;
  private panResponderRight: PanResponderInstance;
  private setIsSwiped(isSwiped: boolean) {
    this.setState({ isSwiped });
  }
  constructor(props: ChatProps) {
    super(props);
    this.state={
      isSwiped:false,
    };
    this.chat=props.chat;
    this.lastMessage=this.chat.listOfMessages.length > 0 ? this.chat.listOfMessages[this.chat.listOfMessages.length - 1] : undefined;
    this.panResponderRight=PanResponder.create({
      onStartShouldSetPanResponder:()=>true,
      onPanResponderMove: (event, gesture) => {
      },

    })
  }
  private formattedTime():string {
    if (!this.lastMessage) return '';
    const now: Date = new Date();
    const timeDiff: number = now.getTime() - this.lastMessage.timeOfSend.getTime();
    const dayInMilliseconds: number = 24 * 60 * 60 * 1000;
    const weekInMilliseconds: number = 7 * dayInMilliseconds;
    const yearInMilliseconds: number = 365 * dayInMilliseconds;
    const minutes: number = this.lastMessage.timeOfSend.getMinutes();
    const hours: number = this.lastMessage.timeOfSend.getHours();
    const day: number = this.lastMessage.timeOfSend.getDate();
    const month: number =this.lastMessage.timeOfSend.getMonth();
    const minutesString: string = minutes < 10 ? `0${minutes}` : minutes.toString();
    const hoursString: string = hours < 10 ? `0${hours}` : hours.toString();
    const daySting: string = day < 10 ? `0${day}` : day.toString();
    const monthString: string = month < 10 ? `0${month}` : month.toString();

    if (timeDiff < dayInMilliseconds) {
      return `${hoursString}:${minutesString}`;
    } else if (timeDiff < weekInMilliseconds) {
      const daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
      const dayOfWeek: string = daysOfWeek[this.lastMessage.timeOfSend.getDay()];
      return dayOfWeek.slice(0, 3);
    } else if (timeDiff < yearInMilliseconds) {
      return `${daySting}.${monthString}`;
    } else {
      return `${daySting}.${monthString}.${this.lastMessage.timeOfSend.getFullYear()-2000}`;
    }
  }
  private handlePress = () => {
    
    console.log('Кнопку натиснули');
  };
  private handleLongPress = () => {
    console.log('Кнопку утримують');
  };
  private renderLeftActions = (progress:any, dragX:any) => {
    const scale=dragX.interpolate({
      inputRange:[0,100],
      outputRange:[0,1],
      extrapolate:"clamp"
    })
    return (
      <View style={listOfChatsStyle.leftAction}>
        <Animated.Text style={[listOfChatsStyle.actionText,{transform:[{scale}]}]}>
          Select
        </Animated.Text>
      </View>
    )
  };

  
  private renderRightActions= (progress:any, dragX:any) => {
    const scale1=progress.interpolate({
      inputRange:[0,1],
      outputRange:[150,0],
    })
    const scale2=progress.interpolate({
      inputRange:[0,1],
      outputRange:[75,0],
    })
    return (
      <View  style={{flexDirection:"row",maxWidth:screenWidth*0.5,minWidth:150}}>
        {!this.state.isSwiped?<Animated.View style={{flex:1,transform:[{translateX:!this.state.isSwiped?scale1:scale2}]}}>
          <RectButton style={[{backgroundColor:"yellow"},listOfChatsStyle.rightAction]}>
            <Text>Notify</Text>
            <Animated.View style={{width:screenWidth,backgroundColor:"yellow",position:"absolute",zIndex:-1,top:0,bottom:0,left:0}}/>
          </RectButton>
        </Animated.View>:<Animated.View style={{flex:1,position:"absolute",zIndex:-1,transform:[{translateX:scale1}]}}>
          <RectButton style={[{backgroundColor:"yellow"},listOfChatsStyle.rightAction]}>
            <Text>Notify</Text>
            <Animated.View style={{width:screenWidth,backgroundColor:"yellow",position:"absolute",zIndex:-1,top:0,bottom:0,left:0}}/>
          </RectButton>
        </Animated.View>}
        <Animated.View style={{flex:1,transform:[{translateX:!this.state.isSwiped?scale2:scale1}]}}>
          <RectButton style={[{backgroundColor:"red"},listOfChatsStyle.rightAction]}>
            <Text>Delete</Text>
          </RectButton>
          <Animated.View style={{width:screenWidth*1.5,backgroundColor:"red",position:"absolute",zIndex:-1,top:0,bottom:0,left:0}}/>
        </Animated.View>
      </View>
    )
  };
  render() {
  return(
  <View>
  <TouchableOpacity 
  onPress={this.handlePress} 
  onLongPress={this.handleLongPress}
  pressRetentionOffset={{ top: 0, left: 0, right: 0, bottom: 0 }}
  activeOpacity={1}
  >
    
    <View style={listOfChatsStyle.helpContainer}/>
    <GestureHandlerRootView>
      <Swipeable renderRightActions={this.renderRightActions}>
    <View   style={listOfChatsStyle.chatcontainer} onPointerMove={(event:PointerEvent)=>{        
        console.log(event.nativeEvent.layout.x)
        if (event.nativeEvent.layout.x <= screenWidth*0.5) {
          this.setIsSwiped(true);
        } else this.setIsSwiped(false)
        

      }
        }>
      <View style={[listOfChatsStyle.imageContainer ]} >
      <Image source={{ uri: this.chat.url }} style={listOfChatsStyle.image}></Image>
      <ModeActivity style={listOfChatsStyle.modeOfActivity} status={this.chat.status}/>
      </View>
      <View style={listOfChatsStyle.containerForOther}>
        <View style={listOfChatsStyle.highcontainer}>
          <View style={listOfChatsStyle.namecontainer}> 
          <Text style={listOfChatsStyle.nameStyle} numberOfLines={1} ellipsizeMode="tail">{this.chat.name}</Text>
          </View>
          <View style={[listOfChatsStyle.rightContainer]} >
            <LastMessageStatus chat={this.chat}/>
             <Text style={listOfChatsStyle.timeStyle}>{this.formattedTime()}</Text>
      </View>
        </View>
        <View style={listOfChatsStyle.containerForContent}>
          <Text style={listOfChatsStyle.contentStyle} numberOfLines={2} ellipsizeMode="tail">{this.lastMessage?.content}</Text>
        </View>
      </View>
    </View>   
      </Swipeable>
    </GestureHandlerRootView>
    </TouchableOpacity>
    </View>
  );
}
};


export default React.memo(ChatContainer);
