import React, { useState } from 'react';
import { View, StyleSheet,Image,Text } from 'react-native';
import Chat from '../../1HelpFullFolder/Chat';
import { listOfChatsStyle } from '../../Styles/ListOfChatsStyle';
interface ChatProps {
  chat: Chat;
}

const ChatContainer: React.FC<ChatProps> = ({ chat }) => {
  
  const [textWidth, setTextWidth] = useState(0);
  const [viewWidth, setViewWidth] = useState(0);

  const handleTextLayout = (event:any) => {
    const { width } = event.nativeEvent.layout;
    setTextWidth(width);
  };

  const handleViewLayout = (event:any) => {
    const { width } = event.nativeEvent.layout;
    setViewWidth(width);
  };
  return (
    <View style={listOfChatsStyle.chatcontainer}>
        <Image source={{uri:chat.url}} style={listOfChatsStyle.image} ></Image>
        <View onLayout={handleTextLayout} style={listOfChatsStyle.containerForNameAndContent}>
        <Text style={listOfChatsStyle.nameStyle}>{chat.name}</Text>
        
        </View>
    </View>
  );
};

export default ChatContainer;
