import React from 'react';
import { View, Text, StyleSheet, StyleProp, ViewStyle, TextStyle } from 'react-native';

const Folder: React.FC<FolderProps> = ({ containerStyle, textStyle, text, isSelected, onPress }) => {
  return (
    <TouchableOpacity onPress={onPress}>
      <View style={[styles.folderContainer, isSelected ? styles.selectedFolder : null, containerStyle]}>
        <Text style={[styles.folder, isSelected ? styles.selectedText : null, textStyle]}>{text}</Text>
      </View>
    </TouchableOpacity>
  );
};


export default Folder;
