import React from "react";
import { View, StyleProp, ViewStyle, Dimensions } from "react-native";
import Svg, { Rect } from "react-native-svg";

interface LineProps {
  screenWidth: number;
  screenHeight: number;
}

const Line: React.FC<LineProps> = ({ screenWidth, screenHeight }) => {
  const centerX = screenWidth * 0.07 / 2; // Розраховуємо центр по ширині фігури

  return (
    <Svg
      width={screenWidth * 0.11}
      height={screenHeight * 0.01}
      viewBox={`0 0 ${screenWidth * 0.11} ${screenHeight * 0.01}`}
      fill="none"
    >
      <Rect

        width={screenWidth * 0.07}
        height={screenHeight * 0.003}
        rx={1.3}
        fill="#A19C91"
        transform={`rotate(45 ${centerX} 0)`} // 45 градусів обертання навколо центру
      />
    </Svg>
  );
};

export default Line;
