import React, { useState } from 'react';
import { View, ScrollView } from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import ChatContainer from './List of folders containers/ChatContainer';
import { listOfChatsStyle } from '../Styles/ListOfChatsStyle';
interface ListOfFolderProps {
  user: MySelfUser;
}

const ListOfFolder: React.FC<ListOfFolderProps> = ({ user }) => {
    let numberOfFolder:number=0;
  return (
    
    <View style={listOfChatsStyle.container}>
      <ScrollView showsHorizontalScrollIndicator={false} >
            {user.folders[0].listOfChats.map((chat, index) => (
                <ChatContainer chat={chat}/>
            ))}
            <View style={listOfChatsStyle.lastContainer}></View>
      </ScrollView>
    </View>
  );
};

export default ListOfFolder;
