import React from 'react';
import { View, StyleSheet,Image,Text } from 'react-native';
import Chat from '../../1HelpFullFolder/Chat';
import { listOfChatsStyle } from '../../Styles/ListOfChatsStyle';
interface ChatProps {
  chat: Chat;
}

const ChatContainer: React.FC<ChatProps> = ({ chat }) => {
  const MAX_TEXT_LENGTH = 35;
const text: string =
  chat.listOfMessages.length > 0
    ? chat.listOfMessages[chat.listOfMessages.length - 1].content
    : "";
const truncatedText1=text.length > MAX_TEXT_LENGTH
? text.slice(0, MAX_TEXT_LENGTH)
: text;
const truncatedText2 =
  text.length > MAX_TEXT_LENGTH
    ? text.slice(MAX_TEXT_LENGTH, MAX_TEXT_LENGTH*2-3) + "..."
    : text;

  return (
    <View style={listOfChatsStyle.chatcontainer}>
        <Image source={{uri:chat.url}} style={listOfChatsStyle.image} ></Image>
        <View style={listOfChatsStyle.containerForNameAndContent}>
        <Text style={listOfChatsStyle.nameStyle}>{chat.name}</Text>
        <Text style={listOfChatsStyle.contentStyle}>{truncatedText1 ? truncatedText1 : null}</Text>
        <Text style={listOfChatsStyle.contentStyle}>{truncatedText2 ? truncatedText2 : null}</Text>
        </View>
    </View>
  );
};

export default ChatContainer;
