import { registerRootComponent } from "expo";
import {
  View,
  StyleSheet,
  Text,
  Dimensions,
  StatusBar,
  TouchableOpacity, // Імпортуємо TouchableOpacity
  AppRegistry,
} from "react-native";
import ChatList from "./Pages/ChatList/ChatList";
import Authorization from './Pages/Authorization/Authorization'; 
import PhoneCodeRegistration from './Pages/Authorization/Pages/PhoneCodeRegistration';
import Registration from './Pages/Authorization/Pages/Registration';
import CodePassword from './Pages/CodePassword/CodePassword'; 
import React from "react";
import { Provider } from "react-redux";
import { createStore } from "redux";
import { rootReducer } from "./ReducersAndActions/Reducers/ChatListActions/ChatListActions";

export default function App() {
  StatusBar.setBarStyle("dark-content");
  const store = createStore(rootReducer);
  
  // Додайте функцію для обробки натискання кнопки "назад"
  const handleBackButtonPress = () => {
    // Додайте код для обробки події "назад", наприклад, для переходу назад на попередній екран
    // Замість цього коду слід реалізувати власну логіку навігації
    console.log("Button 'Back' pressed");
  };

  return (
    <Provider store={store}>
      <View style={style.container}>
        <TouchableOpacity onPress={handleBackButtonPress} style={style.backButton}>
          <Text style={style.backButtonText}>{"<"}</Text>
        </TouchableOpacity>
        <PhoneCodeRegistration />
      </View>
    </Provider>
  );
}

const { width: screenWidth, height: screenHeight } = Dimensions.get("window");
const style = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#E3C07C",
  },
  backButton: {
    position: 'absolute',
    top: 20,
    left: 10,
  },
  backButtonText: {
    fontSize: 24,
    fontWeight: 'bold',
    color: 'white',
  },
});

registerRootComponent(App);
AppRegistry.registerComponent("10", () => App);
AppRegistry.registerComponent("10".toLowerCase(), () => App);
