import React, {useState} from "react";
import { View, Text, TouchableOpacity, Switch } from "react-native";
import StylePassCentre from "./StylePasswordCenter";

const PasswordCenter =()=>{

    const[IsEnable,SetIsEnable]= useState(false);
    const toggleSwitch =() => SetIsEnable(previousState => !previousState)
    
    return<View style={StylePassCentre.passConteiner}>
        <TouchableOpacity style={StylePassCentre.funcOn} ><Text style={StylePassCentre.styleText} >Off lock code</Text> <Switch
        trackColor={{false: '#767577', true: '#81b0ff'}}
        thumbColor={IsEnable ? '#f5dd4b' : '#f4f3f4'}
        ios_backgroundColor="#3e3e3e"
        onValueChange={toggleSwitch}
        value={IsEnable}
      /></TouchableOpacity>
        <TouchableOpacity style={StylePassCentre.funcOn} ><Text style={StylePassCentre.styleText} >Edit lock code</Text><Switch
        trackColor={{false: '#767577', true: '#81b0ff'}}
        thumbColor={IsEnable ? '#f5dd4b' : '#f4f3f4'}
        ios_backgroundColor="#3e3e3e"
        onValueChange={toggleSwitch}
        value={IsEnable}
      /></TouchableOpacity>
        <TouchableOpacity style={StylePassCentre.funcOn} ><Text style={StylePassCentre.styleText} >Unlock to Face or Touch id</Text><Switch
        trackColor={{false: '#767577', true: '#81b0ff'}}
        thumbColor={IsEnable ? '#f5dd4b' : '#f4f3f4'}
        ios_backgroundColor="#3e3e3e"
        onValueChange={toggleSwitch}
        value={IsEnable}
      /></TouchableOpacity>
    </View>
}

export default PasswordCenter;
