import React, { ReactNode, useState } from 'react';
import { View, ScrollView,StyleSheet,Text } from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import { footerstyles } from '../Styles/FooterStyle';
import Folder from './Footer containers/Folder';
import { listOfChatsStyle } from '../Styles/ListOfChatsStyle';
import ChatContainer from './List of folders containers/ChatContainer';

import ListOfFolder from './ListOfFolder';
interface FooterProps {
  user: MySelfUser;
}
export const [swipeable,setSwipeable]=useState<number | null>(null);
const Footer: React.FC<FooterProps> = ({ user }) => {
  const [selectedFolder, setSelectedFolder] = useState<number>(0);
  
  const handleFolderPress = (index: number) => {
    if (selectedFolder === index) {
    } else {
      setSelectedFolder(index); 
    }
  };
 /* const swipeLeft=(numberOfFolder:number):number=>{
    if(numberOfFolder!=0){
      setSelectedFolder(numberOfFolder-1)
      return selectedFolder;
    }
    return numberOfFolder;
  }
  const swipeRight=(numberOfFolder:number):number=>{
    if(numberOfFolder!=user.folders.length-1){
      setSelectedFolder(numberOfFolder+1)
      return selectedFolder;
    }
    return numberOfFolder;
  }*/
  
  return (
    <View>
    <ScrollView showsVerticalScrollIndicator={false}>
      <ListOfFolder user={user} selectedFolder={selectedFolder}  />
    </ScrollView>

<View style={footerstyles.container}>
      <ScrollView showsHorizontalScrollIndicator={false} horizontal={true} style={footerstyles.scrollView}>
        {user.folders.map((folder, index) => (
          <Folder
            key={index}
            text={folder.name}
            containerStyle={footerstyles.folderContainer}
            textStyle={footerstyles.folder}
            isSelected={selectedFolder === index}
            onPress={() => handleFolderPress(index)}
          />
        ))}
      </ScrollView>
    </View>
    </View>
  );
};

export default Footer;
