import React, { ReactNode, useState, useEffect } from 'react';
import { View, ScrollView, StyleSheet, Text } from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import { footerstyles } from '../Styles/FooterStyle';
import Folder from './Footer containers/Folder';
import { listOfChatsStyle } from '../Styles/ListOfChatsStyle';
import ChatContainer from './List of folders containers/ChatContainer';
import Swiper from 'react-native-swiper';
import ListOfFolder from './ListOfFolder';
import { useRef } from 'react';
interface FooterProps {
  user: MySelfUser;
}

const Footer: React.FC<FooterProps> = ({ user }) => {
  const [selectedFolder, setSelectedFolder] = useState<number>(0);

  // Визначте ініціалізований ref для ScrollView
  const scrollViewRef = useRef<ScrollView | null>(null);

  const handleFolderPress = (index: number) => {
    if (selectedFolder === index) {
    } else {
      setSelectedFolder(index); 
    }
  };

  // Додайте ефект useEffect для перерендерення ScrollView при зміні selectedFolder
  useEffect(() => {
    // Отримайте посилання на ScrollView
    const scrollView = scrollViewRef.current;

    // Виконайте прокрутку до обраної папки, якщо потрібно
    if (scrollView) {
      scrollView.scrollTo({ x: selectedFolder * 10, animated: true });
    }
  }, [selectedFolder]);

  return (
    <>
      <Swiper
        loop={false} 
        showsPagination={false} 
        index={selectedFolder}
        onIndexChanged={(newIndex) => {setSelectedFolder(newIndex)}}
      >
        {user.folders.map((folder, index) => (
          <ListOfFolder user={user} selectedFolder={index} key={index} />
        ))}
      </Swiper>
      <View style={footerstyles.container}>
        {/* Додайте ref до ScrollView */}
        <ScrollView 
          ref={scrollViewRef}
          showsHorizontalScrollIndicator={false} 
          horizontal={true} 
          style={footerstyles.scrollView}
        >
          {user.folders.map((folder, index) => (
            <Folder
              key={index}
              text={folder.name}
              containerStyle={footerstyles.folderContainer}
              textStyle={footerstyles.folder}
              isSelected={selectedFolder === index}
              onPress={() => handleFolderPress(index)}
            />
          ))}
        </ScrollView>
      </View>
    </>
  );
};

export default Footer;
