import React, { ReactNode, useRef, useState } from 'react';
import { View, ScrollView,StyleSheet,Text,Dimensions } from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import { footerstyles } from '../Styles/FooterStyle';
import Folder from './Footer containers/Folder';
import { listOfChatsStyle } from '../Styles/ListOfChatsStyle';
import ChatContainer from './List of folders containers/ChatContainer';

import ListOfFolder from './ListOfFolder';
interface FooterProps {
  user: MySelfUser;
}
const { width: screenWidth ,height:screenHeight} = Dimensions.get('window');
const Footer: React.FC<FooterProps> = ({ user }) => {
  const [selectedFolder, setSelectedFolder] = useState<number>(0);
  
  const handleFolderPress = (index: number) => {
    if (selectedFolder === index) {
    } else {
      setSelectedFolder(index); 
    }
  };
  const scrollViewRef = useRef(null);
  const [currentPosition, setCurrentPosition] = useState(0);

  const updateSelectedFolder = () => {
    // Розраховуємо новий вибраний folderId на основі поточної позиції
    const newSelectedFolder = Math.floor(currentPosition / screenWidth); // Ви можете коригувати цю логіку відповідно до своїх потреб
    setSelectedFolder(newSelectedFolder);
  };

  const handleHorizontalScroll = (event:any) => {
    // Отримуємо нову позицію горизонтальної прокрутки
    const newHorizontalPosition = event.nativeEvent.contentOffset.x;

    // Оновлюємо поточну позицію
    setCurrentPosition(newHorizontalPosition);
  };

  const handleMomentumScrollEnd = () => {
    // Викликаємо функцію для оновлення вибраної папки після закінчення прокрутки
    updateSelectedFolder();
  };
  
  return (
    <View>
    <ScrollView horizontal={true}
    ref={scrollViewRef}
    onScroll={handleHorizontalScroll}
    onMomentumScrollEnd={handleMomentumScrollEnd}
    pagingEnabled={true}
    >
      {user.folders.map(folder)}
          <ListOfFolder user={user} selectedFolder={selectedFolder}/>
    </ScrollView>


<View style={footerstyles.container}>
      <ScrollView showsHorizontalScrollIndicator={false} horizontal={true} style={footerstyles.scrollView}>
        {user.folders.map((folder, index) => (
          <Folder
            key={index}
            text={folder.name}
            containerStyle={footerstyles.folderContainer}
            textStyle={footerstyles.folder}
            isSelected={selectedFolder === index}
            onPress={() => handleFolderPress(index)}
          />
        ))}
      </ScrollView>
    </View>
    </View>
  );
};

export default Footer;
