import { registerRootComponent } from "expo";
import {
  View,
  StyleSheet,
  Text,
  Dimensions,
  StatusBar,
  AppRegistry,
  ScrollVi
} from "react-native";
import React from 'react';

const App = () => {
  return (
    <ScrollView
      contentContainerStyle={styles.contentContainer}
      style={styles.scrollView}
    >
      <View style={styles.backgroundView} />
      <View style={styles.centerView}>
        {/* Ваш інший вью, як задній фон */}
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  scrollView: {
    flex: 1,
  },
  contentContainer: {
    flexDirection: 'row', // Встановлюємо горизонтальну орієнтацію
    width: '300%', // Щоб вміст був в 3 рази ширший за екран
  },
  backgroundView: {
    flex: 1,
    backgroundColor: 'lightgray',
    width: '10%', // Лівий і правий 10% від ширини екрану
  },
  centerView: {
    flex: 3, // Середній 80% від ширини екрану
    backgroundColor: 'white',
  },
});



registerRootComponent(App);
AppRegistry.registerComponent("10", () => App);
AppRegistry.registerComponent("10".toLowerCase(), () => App);
