import { StyleSheet, View, Text ,Dimensions} from 'react-native';
import React from 'react';
const { width: screenWidth ,height:screenHeight} = Dimensions.get('window');
export const listOfChatsStyle= StyleSheet.create({
    container:{
        //backgroundColor:"red",

    },
    chatcontainer:{
        flexDirection:"row",
        height:screenHeight*0.08,
        borderBottomColor:"black",
        borderBottomWidth:1
    
    },
    lineStyle:{
        borderBottomColor: '#C4A56899',
        borderBottomWidth: screenWidth*0.02,
    },
    image:{
    height:screenHeight*0.08*0.7,
    width:screenHeight*0.08*0.7,
    aspectRatio: 1,
    alignSelf:"center",
    borderRadius:1000,
    },
    gapContainerHigh:{
        height:screenHeight*0.085
    },
    gapContainerDown:{
        height:screenHeight*0.08
    }
    ,
    containerForOther:{
        flex:1,
        //backgroundColor:"white",
        marginLeft:screenWidth*0.01,
    },
    containerForContent:{
        width:screenWidth*0.7,
        //backgroundColor:"white",
        flex:1
    } ,
    namecontainer:{
        width:screenWidth*0.6,
        //marginTop:screenHeight*0.005,
        paddingBottomBottom:screenHeight*0.003,
        //backgroundColor:"blue",
        paddingTop:screenHeight*0.005
        
    }
    ,
    rightContainer:{
       //backgroundColor:"white",
      // display:"flex",
        flex:1,
        flexDirection:"row",
        justifyContent:"flex-end"
    },
    highcontainer:{
        flexDirection:"row",
    },
    nameStyle:{
        fontWeight:"600",
       fontSize:screenHeight*0.017
    
    },
    contentStyle:{
        fontSize:screenHeight*0.015,
    },
    timeContainer:{
        justifyContent:"flex-end",
        alignSelf:"flex-end",
        marginRight:screenWidth*0.01,
        flexDirection:"row",
        flexBasis:"auto"
       // flex:1,
        //right:screenWidth*0.1,
       // backgroundColor:"gray"
    },
    timeStyle:{
        alignSelf:"flex-end",
        fontSize:screenHeight*0.014,
        color:"#222222",
        position:"relative",
        marginRight:screenWidth*0.01,
        
    },
    lastMessageStatusContainer:{
        position:"relative",
        alignSelf:"flex-end",
        justifyContent:"flex-end",
        flexDirection:"row",
        marginRight:screenWidth*0.01,

    },
    countOfUnreadMessagescontainer:{
        backgroundColor:"#339EDA",
        textAlign:"center",
        borderRadius: 50,
        fontSize:screenWidth*0.025,
        maxWidth:"100%",
        justifyContent:"center",
    },
    oneCharcontainer:{
        width:screenWidth*0.034,
        
    },
    twoCharcontainer:{
        width:screenWidth*0.055
    },
    threeCharcontainer:{
        width:screenWidth*0.066
    },
    fourCharcontainer:{
        width:screenHeight*0.08
    },
    countOfUnReadMessagesContent:{
        fontSize:screenHeight*0.014,
        color:"#FFFFFF",
        alignSelf:"center"
    },
    checkMarkercontainerStyle:{
        height:screenHeight*0.03,
        justifyContent:"flex-end",
        alignSelf:"flex-end",
        flexDirection:"row",
        marginLeft:screenWidth*0.01
        //right:screenWidth*0.01,
    
    }
    ,
    checkMarkStyle:{
        width:screenWidth*0.03,
        aspectRatio:1
    },
    positionOfFirstCheckMarkStyle: {
        position: "relative" ,
        alignSelf:"flex-end"
      },
      positionOfSecondCheckMarkStyle: {
        position: "absolute",  
        left: screenWidth * 0.01,
        alignSelf:"flex-end"
    }
});
