import React, { useState } from "react";
import {
  View,
  Text,
  Dimensions,
} from "react-native";
import { footerstyles } from "../../Styles/FooterStyle";
import Folder from "../../1HelpFullFolder/Folder";

interface FolderModalWindowProps {
  folder: Folder;
  positionX: number;
  positionXInContainer:number;
  widthOfFolder: number;

}
const { width: screenWidth, height: screenHeight } = Dimensions.get("window");
const FolderModalWindow: React.FC<FolderModalWindowProps> = ({
  folder,
  positionX,
  widthOfFolder,
  positionXInContainer
}) => {
  return (
    <View style={[footerstyles.modalWindowContainerStyle,{left:positionX-positionXInContainer+widthOfFolder/2}]}>
      <View style={footerstyles.containerForModalWindowText}>
        <Text style={footerstyles.modalWindowText}>Add chat</Text>
      </View>
      <View style={footerstyles.containerForModalWindowText}>
        <Text style={footerstyles.modalWindowText}>Edit folder</Text>
      </View>
      <View style={footerstyles.containerForModalWindowText}>
        <Text style={footerstyles.modalWindowText}>On notifications</Text>
      </View>
      <View style={footerstyles.containerForModalWindowText}>
        <Text style={footerstyles.modalWindowText}>Sort folders</Text>
      </View>
      <View style={footerstyles.containerForModalWindowText}>
        <Text style={[footerstyles.modalWindowText,{color:"red"}]}>Delete</Text>
      </View>
      <View style={[footerstyles.triangle,positionX-positionXInContainer+widthOfFolder/2<screenWidth*0.65?footerstyles.positionOfModalWindowLeft:footerstyles.positionOfModalWindowRight]}/>
    </View>
  );
};

export default FolderModalWindow;
