import { StyleSheet, View, Text, Dimensions } from "react-native";

const { width: screenWidth, height: screenHeight } = Dimensions.get("window");
export const footerstyles = StyleSheet.create({
  container: {
    position: "absolute",
    bottom: 0,
    left: 0,
    right: 0,
    backgroundColor: "#E7E6E4",
    flex: 1,
    height: screenHeight * 0.08,
    borderTopLeftRadius: 30,
    borderTopRightRadius: 30,
    borderWidth: 1.8,
    flexDirection: "column",
    zIndex: 10,
  },
  scrollView: {
    marginLeft: screenWidth * 0.04,
    marginRight: screenWidth * 0.04,
    //alignSelf: 'center',
    width: screenWidth * 0.92,
  },
  folderContainer: {
    marginTop: screenHeight * 0.01,
    padding: screenWidth * 0.017,
  },
  folder: {
    fontSize: screenHeight * 0.017,
  },
  selectedText: {
    color: "#7C4F91",
    fontSize: screenHeight * 0.017,
  },
  selectedFolder: {
    height: screenHeight * 0.005,
    backgroundColor: "#7C4F91",
    borderRadius: 10,
  },
  modalWindowContainerStyle:{
    position:"absolute",
    bottom:screenHeight*0.085,
  },
  containerForModalWindowText:{
    backgroundColor:"#E7E6E4",
    width:screenWidth*0.3,
    height:screenHeight*0.03,
    borderRadius:100,
    justifyContent:"center",
  },
  modalWindowText:{
    fontSize:screenHeight*0.017,
    marginLeft:screenWidth*0.03
  },
  triangle:{
    width: 0,
    height: 0,
    backgroundColor: 'transparent',
    borderStyle: 'solid',
    borderLeftWidth: screenWidth*0.015, 
    borderRightWidth: screenWidth*0.01, 
    borderBottomWidth: screenWidth*0.02, 
    borderLeftColor: 'transparent',
    borderRightColor: 'transparent',
    borderBottomColor: 'red', 
    transform: [{ rotate: '90deg' }],
    position:"relative",
    bottom:screenHeight*0.005,
    left:screenWidth*0.01
  }
});
