import React, { useState } from 'react';
import { View, ScrollView, StyleSheet, Text, FlatList } from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import { footerstyles } from '../Styles/FooterStyle';
import Folder from './Footer containers/Folder';
import ListOfFolder from './ListOfFolder';

interface FooterProps {
  user: MySelfUser;
}

const Footer: React.FC<FooterProps> = ({ user }) => {
  const [selectedFolder, setSelectedFolder] = useState<number>(0);

  const handleFolderPress = (index: number) => {
    setSelectedFolder(index);
  };

  const renderItem = ({ item, index }: { item: MySelfUser; index: number }) => (
    <ListOfFolder user={item} selectedFolder={selectedFolder} key={index} />
  );

  return (
    <>
      <FlatList
  data={[user]}
  renderItem={({ item }) => (
    <FlatList
      data={item.folders}
      horizontal={true} // Встановлюємо горизонтальну прокрутку
      showsHorizontalScrollIndicator={false}
      renderItem={renderItem} // Ваша функція рендерингу елементів списку
      keyExtractor={(folder) => folder.id.toString()} // Ваша функція видобування ключів
    />
  )}
  showsVerticalScrollIndicator={false}
  keyExtractor={(user) => user.id.toString()} // Ваша функція видобування ключів користувачів (якщо потрібно)
/>
      <View style={footerstyles.container}>
        <ScrollView
          showsHorizontalScrollIndicator={false}
          horizontal={true}
          style={footerstyles.scrollView}
        >
          {user.folders.map((folder, index) => (
            <Folder
              key={index}
              text={folder.name}
              containerStyle={footerstyles.folderContainer}
              textStyle={footerstyles.folder}
              isSelected={selectedFolder === index}
              onPress={() => handleFolderPress(index)}
            />
          ))}
        </ScrollView>
      </View>
    </>
  );
};

export default Footer;
