import React, {useState} from 'react';
import { View, Image, Button, Text,TouchableOpacity, Modal, StyleSheet  } from 'react-native';
import StyleCentre from './StyleCenter';
import ButtonList from './ButtonLIst/Buttonlist';
import StyleButtonList from './ButtonLIst/StyleButtonLIst';
import stylesModal from './ModalWindowSyle';

const Center =()=>{
        const [isModalVisible, setModalVisible] = useState(false);
        const toggleModal = () => {
            setModalVisible(!isModalVisible);
        };

    return <View style={StyleCentre.conteiner} >
           <Image style={StyleCentre.imgStyle} source={{uri:'https://www.meme-arsenal.com/memes/a297a80a2839282005e0a60135421919.jpg'}}></Image>
           <View style={StyleCentre.button}><Button title ="Edit" ></Button></View>
           <View style={StyleCentre.box}></View>
           <Text style= {StyleCentre.text}>Settings</Text>
           <ButtonList></ButtonList>
           <Text style= {StyleCentre.text}>Help</Text>
           <TouchableOpacity style={StyleButtonList.button}>
                <Text>Question in Telintik</Text>
           </TouchableOpacity>
            <TouchableOpacity style={StyleButtonList.button}>
                <Text>Ask a Question</Text>
            </TouchableOpacity>
            <TouchableOpacity style ={StyleCentre.editButton} >
                <Text style={StyleCentre.editButtonText} onPress={toggleModal}>Exit</Text>
            </TouchableOpacity>            
           <View style={stylesModal.container}>
           <Modal
                    animationType="none"
                    transparent={true}
                    visible={isModalVisible}
                    onRequestClose={toggleModal}
                >
                    <View style={stylesModal.modalContainer}>
                    <View style={stylesModal.modalContent}>
                        <View style={stylesModal.modalContainer} ><Text>Do you reallu want to log out</Text></View>
                        <View style={stylesModal.modalButtonConteir}>
                            <TouchableOpacity style={stylesModal.modalButtons} onPress={toggleModal} ><Text>Agree</Text></TouchableOpacity>
                            <TouchableOpacity style={stylesModal.modalButtons}  onPress={toggleModal} ><Text>Unagree</Text></TouchableOpacity>
                        </View>
                    </View>
                    </View>
            </Modal>
           </View>
    </View>
}

export default Center;
