import React, { Component } from 'react';
import {
  AppRegistry,
  StyleSheet,
  Text,
  View,
  ScrollView,
  PanResponder,
  GestureResponderEvent,
  PanResponderGestureState,
} from 'react-native';
import { GestureHandlerRootView } from 'react-native-gesture-handler';


class ScrollPan extends React.Component {
  private wrapperPanResponder: any;
  private scollerPanResponder: any;

  constructor() {
    super(0)

    this.wrapperPanResponder = PanResponder.create({
      onStartShouldSetPanResponder: (e: GestureResponderEvent, g: PanResponderGestureState) => true,
      onPanResponderGrant: () => {
        console.log('GRANTED TO WRAPPER');
      },
      onPanResponderMove: (evt: GestureResponderEvent, gestureState: PanResponderGestureState) => {
        console.log('WRAPPER MOVED');
      },
    });

    this.scollerPanResponder = PanResponder.create({
      onStartShouldSetPanResponder: (e: GestureResponderEvent, g: PanResponderGestureState) => true,
      onPanResponderGrant: () => {
        console.log('GRANTED TO SCROLLER');
      },
      onPanResponderMove: (evt: GestureResponderEvent, gestureState: PanResponderGestureState) => {
        console.log('SCROLLER MOVED');
      },
    });
  }

  render() {
    return (
      <View style={styles.container}>
        <View style={styles.pan_container} {...this.wrapperPanResponder.panHandlers} />
        <GestureHandlerRootView>
            
        </GestureHandlerRootView>
        <ScrollView onScroll={() => console.log('scrolled')} style={styles.scroll_view} {...this.scollerPanResponder.panHandlers}>
          <Text style={{ fontSize: 96 }}>Scroll this</Text>
          <Text style={{ fontSize: 96 }}>Scroll this</Text>
          <Text style={{ fontSize: 96 }}>Scroll this</Text>
          <Text style={{ fontSize: 96 }}>Scroll this</Text>
          <Text style={{ fontSize: 96 }}>Scroll this</Text>
        </ScrollView>
      </View>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '#F5FCFF',
  },
  pan_container: {
    ...StyleSheet.absoluteFillObject,
    backgroundColor: 'blue',
  },
  scroll_view: {
    backgroundColor: 'teal',
    maxHeight: 350,
  },
});


export default React.memo(ScrollPan)
