import React from 'react';
import { StyleSheet, View, Text, Dimensions } from 'react-native';

const { width: screenWidth, height: screenHeight } = Dimensions.get('window');

export const headerstyles = StyleSheet.create({
  container: {
    justifyContent: 'center',
    height: '8%',
    backgroundColor: '#E7E6E4',
    borderBottomLeftRadius: 30,
    borderBottomRightRadius: 30,
    borderWidth: 1.8,
    borderColor: '#434343',
    flexDirection: 'row', // Додано напрямок розміщення елементів в рядок
    alignItems: 'center', // Відцентровує дітей по вертикалі
  },
  magnifyingglass: {
    height: '45%',
    color: '#434343',
    display: 'flex',
    alignSelf: 'flex-start',
    marginLeft: '5%',
  },
  containerforavatar: {
    flex: 1, 
    display: 'flex',
    alignItems: 'center', 
    justifyContent: 'center', // Відцентровує аватар по горизонталі
  },
  avatar: {
    width: screenWidth * 0.1,
    height: screenWidth * 0.1,
    aspectRatio: 1,
  },
});
