import React from 'react';
import { StyleSheet, View, Text ,Dimensions} from 'react-native';

const { width: screenWidth ,height:screenHeight} = Dimensions.get('window');
export const headerstyles = StyleSheet.create({
    container: {
      height:'8%',
      backgroundColor:'#E7E6E4',
      borderBottomLeftRadius:30,
      borderBottomRightRadius:30,
      borderWidth:1.8,
      borderColor:'#434343',
      display:"flex",
      flexDirection:"row",
      
    },
    magnifyingglass:{
        height:'45%',
        color:"#434343",
        display:"flex",
        alignSelf:"center",
        marginLeft:"5%"
    },
    containerforavatar: {
      flex: 1, 
      display: 'flex',
      alignItems: 'center', 
      justifyContent: 'center',
       
      },
    avatar: {
      width: screenWidth * 0.1,
      aspectRatio: 1,
      borderRadius:1000,
    },
    modeactivity:{
      width:screenWidth*3,
      aspectRatio:1,
      position:"relative",
      top:-8,
      left:13,
    }
  });
