import React,{useState} from "react";
import { View, Text, TouchableOpacity, Switch } from "react-native";
import StylePassCentre from "./StylePasswordCenter";

const MultipleSwitches = () => {
    const [switch1, setSwitch1] = useState(false);
    const [switch2, setSwitch2] = useState(false);

    const pressSwitch1 =()=>{
        setSwitch1((prev) => !prev);
        if (switch1) {
            setSwitch2(true);
          } else {
            setSwitch2(false)
          }
    }
    return<View style={StylePassCentre.passConteiner}>
    <TouchableOpacity style={StylePassCentre.funcOn} ><Text style={StylePassCentre.styleText} >Off lock code</Text>
    <Switch
      style ={StylePassCentre.styleSwitch}
      trackColor={{ false: "#767577", true: "#81b0ff" }}
      thumbColor={switch1 ? "#f5dd4b" : "#f4f3f4"}
      ios_backgroundColor="#3e3e3e"
      onValueChange={() => setSwitch1((prev) => !prev)}
      value={switch1}
    /> 
    </TouchableOpacity>
    <TouchableOpacity style={StylePassCentre.funcOn} ><Text style={StylePassCentre.styleText} >Edit lock code</Text>
    </TouchableOpacity>
    <TouchableOpacity style={StylePassCentre.funcOn} ><Text style={StylePassCentre.styleText} >Unlock to Face or Touch id</Text>
    <Switch 
      style ={StylePassCentre.styleSwitch}
      trackColor={{ false: "#767577", true: "#81b0ff" }}
      thumbColor={switch2 ? "#f5dd4b" : "#f4f3f4"}
      ios_backgroundColor="#3e3e3e"
      onValueChange={() => setSwitch2((prev) => !prev)}
      value={switch2}
    />
    </TouchableOpacity>
</View>
}

export default MultipleSwitches;
