import { View, ScrollView} from 'react-native';
import MySelfUser from '../1HelpFullFolder/MySelfUser';
import { listOfChatsStyle } from '../Styles/ListOfChatsStyle';
import ChatContainer from './List of folders containers/ChatContainer';
import {SwipeListView} from 'react-native-swipe-list-view';
interface ListOfFolderProps {
  user: MySelfUser;
  selectedFolder:number
}

const ListOfFolder: React.FC<ListOfFolderProps> = ({ user,selectedFolder }) => {
  

  return (
    <>
    <ScrollView showsVerticalScrollIndicator={false} style={listOfChatsStyle.container}>
      <View style={listOfChatsStyle.gapContainerHigh} />
      <View style={listOfChatsStyle.lineStyle}/>
      <SwipeListView data={user.folders[selectedFolder]}
        renderItem={(data,index)=>{
          return(<ListOfFolder  key={data.index} user={user} selectedFolder={data.index} />)
        }}
      ></SwipeListView>

      <View style={listOfChatsStyle.gapContainerDown} />
    </ScrollView>


    </>
  );
};

export default ListOfFolder;
