import React, { useRef, useState, useEffect } from "react";
import {
  View,
  ScrollView,
  Dimensions,
  Animated,
  LayoutChangeEvent,
  GestureResponderEvent,
  Text,
  TouchableOpacity,
} from "react-native";
import MySelfUser from "../1HelpFullFolder/MySelfUser";
import { footerstyles } from "../Styles/FooterStyle";
import ListOfFolder from "./ListOfFolder";
import FolderContainer from "./Footer containers/FolderContainer";
import FolderModalWindow from "./Footer containers/FolderModalWindow";
import { BlurView } from "expo-blur";
interface FooterProps {
  user: MySelfUser;
}

const { width: screenWidth, height: screenHeight } = Dimensions.get("window");

const Footer: React.FC<FooterProps> = ({ user }) => {
  const [selectedFolder, setSelectedFolder] = useState<number>(0);
  const [selectedLongPressFolder, setSelectedLongPressFolder] =
    useState<number>(0);
  const scrollX = useRef(new Animated.Value(0)).current;
  const [currentPosition, setCurrentPosition] = useState(0);
  const handleFolderPress = (index: number) => {
    if (selectedFolder === index) {
      return;
    }
    setSelectedFolder(index);
    scrollToFolder(index);
  };
  const scrollToIcon = (index: number): void => {
    scrollViewRefFooter?.current?.scrollTo({
      x:
        positionsOfFolder[index] - (screenWidth * 0.92) / 2 + widths[index] / 2,
      animated: true,
    });
  };
  const updateSelectedFolder = () => {
    const newSelectedFolder = Math.round(currentPosition / screenWidth);
    scrollToIcon(newSelectedFolder);
    setSelectedFolder(newSelectedFolder);
  };

  const handleHorizontalScroll = (event: any) => {
    let newHorizontalPosition = event.nativeEvent.contentOffset.x;
    if (newHorizontalPosition < 0) {
      newHorizontalPosition = 0;
    } else if (
      newHorizontalPosition >
      screenWidth * (user.folders.length - 1)
    ) {
      newHorizontalPosition = screenWidth * (user.folders.length - 1);
    }
    setCurrentPosition(newHorizontalPosition);
  };

  const handleMomentumScrollEnd = () => {
    updateSelectedFolder();
  };

  const scrollToFolder = (folderId: number) => {
    scrollViewRef.current?.scrollTo({
      x: folderId * screenWidth,
      animated: true,
    });
    scrollToIcon(folderId);
  };

  useEffect(() => {
    scrollX.addListener(({ value }) => {
      setCurrentPosition(value);
    });
    return () => {
      scrollX.removeAllListeners();
    };
  }, []);

  const scrollViewRef = useRef<ScrollView | null>(null);
  const scrollViewRefFooter = useRef<ScrollView | null>(null);
  const [widths, setWidths] = useState<number[]>(
    user.folders.map(() => screenWidth * 0.1)
  );

  const viewsRefs: any = [user.folders.forEach(() => useRef(null))];
  const [positionsOfFolder, setPositionOfFolder] = useState<number[]>(
    user.folders.map(() => 0)
  );
  const handleLayout = (event: LayoutChangeEvent, index: number) => {
    const { width } = event.nativeEvent.layout;
    const position = event.nativeEvent.layout.x;
    const updatePosition = [...positionsOfFolder];
    updatePosition[index] = position;
    setPositionOfFolder(updatePosition);
    const updatedWidths = [...widths];
    updatedWidths[index] = width;
    setWidths(updatedWidths);
  };
  const [isVisibleForModalFolder, setisVisibleForModalFolder] = useState(false);
  const [positionX, setPositionX] = useState<number>(0);
  const [positionXInContainer, setPositionXInContainer] = useState<number>(0);
  const handleLongPress = (e: GestureResponderEvent, index: number) => {
    setSelectedLongPressFolder(index);
    const target = e.nativeEvent;
    setPositionX(target.pageX);
    setPositionXInContainer(target.locationX);
    if (target.pageX - target.locationX + widths[index] > screenWidth * 0.98) {
      scrollViewRefFooter.current?.scrollTo({
        x: positionsOfFolder[index] - screenWidth * 0.92 + widths[index],animated:false
      });
      setPositionX(screenWidth * 0.964 - widths[index]);
      setPositionXInContainer(0);
    } else if (target.pageX - target.locationX < screenWidth * 0.04) {
      scrollViewRefFooter.current?.scrollTo({ x: positionsOfFolder[index],animated:false });
      setPositionX(screenWidth * 0.044);
      setPositionXInContainer(0);
    }
    setisVisibleForModalFolder(true)
  };

  const [startTime, setStartTime] = useState(0);
  const [endTime, setEndTime] = useState(0);
  const handlePress = () => {
    setStartTime(Date.now());
  };

  const handlePressOut = () => {
    setEndTime(Date.now());
    const duration = startTime - endTime;
    if (duration < 16) {
      setisVisibleForModalFolder(false);
      return;
    }
    setStartTime(Date.now());
  };
  const handleLongPressForChatModalWindow = (e: GestureResponderEvent, index: number) => {
    console.log()
  }
  return (
    <>
      {isVisibleForModalFolder ? (
        <TouchableOpacity
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            zIndex: 10,
          }}
          onPress={handlePress}
          onPressOut={handlePressOut}
          onLongPress={handlePress}
          activeOpacity={1}
        >
          <BlurView 
          style={{
            position: "absolute",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
          }}intensity={40} />
          <FolderModalWindow
            folder={user.folders[selectedLongPressFolder]}
            positionX={positionX}
            positionXInContainer={positionXInContainer}
            widthOfFolder={widths[selectedLongPressFolder]}
          />
          <TouchableOpacity
            activeOpacity={1}
            style={[
              footerstyles.folderContainer,
              {
                zIndex: 10,
                position: "absolute",
                left: positionX - positionXInContainer,
                bottom: screenHeight * 0.032,
                borderRadius:100,
                height:screenHeight*0.036,
                backgroundColor:"#E7E6E4",
                shadowColor:"black",
                shadowOffset:{width:1,height:1},
                shadowOpacity:0.3
                
              },
            ]}
          >
            <Text
              style={[
                selectedLongPressFolder == selectedFolder
                  ? footerstyles.selectedText
                  : footerstyles.folder,
                  
              ]}
            >
              {user.folders[selectedLongPressFolder].name}
            </Text>
          </TouchableOpacity>
        </TouchableOpacity>
      ) : null}
      <ScrollView
        horizontal
        scrollEventThrottle={16}
        showsHorizontalScrollIndicator={false}
        ref={scrollViewRef}
        pagingEnabled
        onScroll={handleHorizontalScroll}
        onMomentumScrollEnd={handleMomentumScrollEnd}
        nestedScrollEnabled={true}
      >
        {user.folders.map((folder, index) => (
          <ListOfFolder key={index} user={user} selectedFolder={index} />
        ))}
      </ScrollView>

      <View style={footerstyles.container}>
        <ScrollView
          showsHorizontalScrollIndicator={false}
          horizontal
          style={footerstyles.scrollView}
          scrollEventThrottle={1}
          ref={scrollViewRefFooter}
        >
          <View>
            <View style={{ flexDirection: "row" }}>
              {user.folders.map((folder, index) => {
                return (
                  <View
                    key={index}
                    onLayout={(event) => handleLayout(event, index)}
                    ref={viewsRefs[index]}
                    style={[isVisibleForModalFolder ? { zIndex: 10 } : null]}
                  >
                    <FolderContainer
                      key={index}
                      folder={folder}
                      containerStyle={footerstyles.folderContainer}
                      textStyle={footerstyles.folder}
                      isSelected={
                        !isVisibleForModalFolder&&index === Math.round(currentPosition / screenWidth)
                      }
                      onPress={() => handleFolderPress(index)}
                      width={widths[index]}
                      handleLongPress={(e) => handleLongPress(e, index)}
                    />
                  </View>
                );
              })}
            </View>
            <Animated.View
              style={{
                left:
                  (((currentPosition -
                    screenWidth * Math.round(currentPosition / screenWidth)) %
                    screenWidth) /
                    screenWidth) *
                    widths[Math.round(currentPosition / screenWidth)] +
                  positionsOfFolder[Math.round(currentPosition / screenWidth)],
              }}
            >
              {!isVisibleForModalFolder ? (
                <Animated.View
                  style={[
                    footerstyles.selectedFolder,
                    {
                      width:
                        widths[Math.round(currentPosition / screenWidth)] * 0.8,
                      marginLeft:
                        widths[Math.round(currentPosition / screenWidth)] * 0.1,
                    },
                  ]}
                />
              ) : null}
            </Animated.View>
          </View>
        </ScrollView>
      </View>
    </>
  );
};

export default Footer;
