import React from 'react';
import { View, Text, TextInput, TouchableOpacity} from 'react-native';
import { styles } from '../Style/Style';
import Icon from 'react-native-vector-icons/FontAwesome';
import ArrowView from 'react-native-vector-icons/FontAwesome';
import Svg, { Polygon } from 'react-native-svg';

export default function PhoneCodeRegistration() {
  return (
    <View style={styles.container}>
     
      <Text style={styles.header}>Code for phone</Text>

      <View style={styles.contentContainer}>
      <TouchableOpacity
        style={styles.backButton}

        onPress={() => {
          // обробка події для кнопки "назад" тут
        }}
      >
        <Icon name="chevron-left" style={styles.backButtonText} />
      </TouchableOpacity>
        <Text style={styles.label}>Write code</Text>
        <TextInput
          style={styles.input}
          placeholder="7788"
        />
        
        <Text style={styles.sentCodeAgainButtonText}>Sent code again</Text>
        <TouchableOpacity
          style={styles.signInButton}
        >
        <Text style={styles.signInLaber}>Finish <CustomArrowView length={30} /> 
        </Text>
        </TouchableOpacity>
      
      </View> 
    </View>
  );
}
const CustomArrowView = ({ length }) => (
  <View style={{ flexDirection: 'row', alignItems: 'center', marginLeft: 5 }}>
    <View
      style={{
        width: length / 2,
        height: 10,
        backgroundColor: '#fff', // Arrow color
      }}
    />
    <View
      style={{
        width: length / 4,
        height: 20,
        backgroundColor: '#fff', // Arrow color
        borderBottomLeftRadius: 10,
        borderBottomRightRadius: 10,
      }}
    />
    <View
      style={{
        width: length / 4,
        height: 10,
        backgroundColor: '#fff', // Arrow color
      }}
    />
  </View>
);
