import React, { Component, useState } from "react";
import {
  StyleSheet,
  Platform,
  StatusBar,
  Dimensions,
  View,
  Text,
} from "react-native";

import * as Font from "expo-font";

let customFonts = {
  JacquesFrancois: require("./DialogueProfileAssets/JacquesFrancois-Regular.ttf"),
};

export class JacquesFrancoisText extends React.Component<
  { text: string; style?: any },
  { fontsLoaded: boolean }
> {
  state = {
    fontsLoaded: false,
  };

  async _loadFontsAsync() {
    await Font.loadAsync(customFonts);
    this.setState({ fontsLoaded: true });
  }

  componentDidMount() {
    this._loadFontsAsync();
  }

  render() {
    if (!this.state.fontsLoaded) {
      return null;
    }

    const { text, style } = this.props;
    return (
      <Text style={{ fontFamily: "JacquesFrancois", fontSize: 30 }}>
        {this.props.text}
      </Text>
    );
  }
}

const figmaHeightPixelConverter = Dimensions.get("screen").height / 648;
const figmaWidthPixelConverter = Dimensions.get("screen").width / 356;

export const styles = StyleSheet.create({
  mainContainer: {
    flex: 1,
    backgroundColor: "rgb(218, 182, 113)",
  },
  topToolBar: {
    backgroundColor: "rgb(231, 230,	228)",
    width: "100%",
    alignItems: "center",
    height: 73 * figmaHeightPixelConverter,
    borderTopLeftRadius: 0, // Радіуси для верхнього кута
    borderTopRightRadius: 0,
    borderBottomLeftRadius: 40, // Радіуси для нижнього кута
    borderBottomRightRadius: 40,
    borderWidth: 1.5,
    borderColor: "rgb(161, 156, 145)",
    justifyContent: "center",
  },
  profileTitle: {
    fontSize: 28,
    alignSelf: "center",
    position: "absolute",
    fontFamily: "JacquesFrancois",
  },
  onlineStatusTitle: {
    fontSize: 14,
    color: "#808080",
    alignSelf: "center",
    fontFamily: "JacquesFrancois",
    top: 27,
  },
  mainAvatarImage: {
    width: 94 * figmaHeightPixelConverter,
    height: 94 * figmaHeightPixelConverter,
    borderRadius: 100,
    justifyContent: "center",
    alignContent: "center",
  },
  goBackFromProfileButton: {
    position: "absolute",
    top: 0.045 * Dimensions.get("screen").height,
    alignSelf: "flex-start",
    left: 0.045 * Dimensions.get("screen").width,
  },
  searchMessagesButton: {
    position: "absolute",
    alignSelf: "flex-end",
    right: 0.175 * Dimensions.get("screen").width,
    top: 0.045 * Dimensions.get("screen").height,
  },
  elseFeaturesButton: {
    position: "absolute",
    alignSelf: "flex-end",
    right: 0.06 * Dimensions.get("screen").width,
    top: 0.045 * Dimensions.get("screen").height,
  },
});
