import React, { useState } from 'react';
import { View, StyleSheet,Image,Text } from 'react-native';
import Chat from '../../1HelpFullFolder/Chat';
import { listOfChatsStyle } from '../../Styles/ListOfChatsStyle';
import Message from '../../1HelpFullFolder/Message';
interface ChatProps {
  chat: Chat;
}

const ChatContainer: React.FC<ChatProps> = ({ chat }) => {
  let lastMessage:Message=chat.listOfMessages[chat.listOfMessages.length-1];
  return (
    <View style={listOfChatsStyle.chatcontainer}>
        <Image source={{uri:chat.url}} style={listOfChatsStyle.image} ></Image>
        <View  style={listOfChatsStyle.containerForNameAndContent}>
          <Text style={listOfChatsStyle.nameStyle}>{chat.name}</Text>
          <Text style={listOfChatsStyle.contentStyle}numberOfLines={2} ellipsizeMode="tail">{lastMessage?.content}</Text>
        </View>
        <View style={listOfChatsStyle.rightContainer}>
          <Text style={listOfChatsStyle.timeStyle}>{GetTime(lastMessage?.timeOfSend)}</Text>
        </View>
    </View>
  );
};
function GetTime(time: Date): string {
  const now: Date = new Date();
  const timeDiff: number = now.getTime() - time.getTime();
  const dayInMilliseconds: number = 24 * 60 * 60 * 1000; 
  const weekInMilliseconds: number = 7 * dayInMilliseconds; 
  const yearInMilliseconds: number = 365 * dayInMilliseconds; 
  const minutes:string=time.getMi()<10?`0${time.getUTCMonth()}`:time.getMonth()
  if (timeDiff < dayInMilliseconds) {
    const hours: number = Math.floor(timeDiff / (60 * 60 * 1000));
    const minutes: number = Math.floor((timeDiff % (60 * 60 * 1000)) / (60 * 1000));
    return `${time.getHours()}:${time.getUTCMinutes()}`;
  } else if (timeDiff < weekInMilliseconds) {
    const daysOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    const dayOfWeek: string = daysOfWeek[time.getDay()];
    return dayOfWeek;
  } else if (timeDiff < yearInMilliseconds) {
    return `${time.getDay} ${time.getMonth}`;
  } else {
    const month: string = time.toLocaleDateString(undefined, { month: 'long' });
    const day: number = time.getDate();
    const year: number = time.getFullYear();
    return `${time.getUTCDate()}:${10}:${time.getFullYear()}`;
  }
  return "";
}

export default ChatContainer;
